sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue == '') {
		return;
	}

	if (newValue == oldValue) {
		return;
	}

	var requestBody = '';
	var restEndPoint = "api/x_snc_it_purchase/itses_field_controls/getfieldcontrols/";
	restEndPoint = restEndPoint + "x_snc_it_purchase_request_request/"; //Table Name
	restEndPoint = restEndPoint + oldValue + "/" + newValue;
	var client=new XMLHttpRequest();
	client.open("get",restEndPoint, false);
	client.setRequestHeader('Accept','application/json');
	client.setRequestHeader('Content-Type','application/json');
	client.send(requestBody);
	if (client.status === 200) {
		var serverResponse = JSON.parse(client.response);
		var fieldControls = serverResponse.result;
		var mandatory = fieldControls.mandatory.toString().split(',');
		var readonly = fieldControls.readonly.toString().split(',');
		var visible = fieldControls.visible.toString().split(',');
		var notmandatory = fieldControls.notmandatory.toString().split(',');
		var notreadonly = fieldControls.notreadonly.toString().split(',');
		var notvisible = fieldControls.notvisible.toString().split(',');
		for (var i=0; i < mandatory.length; i++)
			if (mandatory[i])
			g_form.setMandatory(mandatory[i], true);
		for (var i=0; i < notmandatory.length; i++)
			if (notmandatory[i])
			g_form.setMandatory(notmandatory[i], false);
		for (var i=0; i < readonly.length; i++)
			if (readonly[i])
			g_form.setReadOnly(readonly[i], true);
		for (var i=0; i < notreadonly.length; i++)
			if (notreadonly[i])
			g_form.setReadOnly(notreadonly[i], false);
		for (var i=0; i < visible.length; i++)
			if (visible[i])
			g_form.setDisplay(visible[i], true);
		for (var i=0; i < notvisible.length; i++)
			if (notvisible[i])
			g_form.setDisplay(notvisible[i], false);
	}

	/*var stateFlow = new GlideRecord("x_snc_it_purchase_sf_request_request");
