    function ifScript() {		
		var rec = new GlideRecord('x_snc_it_purchase_request_task');
		rec.addQuery('parent', current.sys_id); 
		rec.addQuery('state', 4);
		rec.query();
		if (rec.next()) {
			return 'no';
		} else {
			return 'yes';
		}	
	}

