//All Approvals are complete 
var rec = new GlideRecord('sysapproval_approver');
rec.addQuery('sysapproval', current.sys_id); 
rec.query();
while (rec.next()) {
	if (rec.state == 'approved') {
		rec.state = 'approved_final';
		rec.update();	
	}    
}

